import { Component } from '@angular/core';
import { Picture } from 'src/app/models/picture';
import { PictureListService } from 'src/app/services/picture-list.service';

@Component({
  selector: 'app-gallery',
  templateUrl: './gallery.component.html',
  styleUrls: ['./gallery.component.css'],
  providers:[PictureListService]
})
export class GalleryComponent {
  pictures:Picture[]=[];
  selectedItem:Picture=this.pictures[0];


  ImgClick(picture:Picture){
    this.selectedItem=picture;
  }


  constructor(private pictureListService:PictureListService){

  }

  ngOnInit(){
    this.pictures=this.pictureListService.GetData();
    this.selectedItem=this.pictures[0];
  }
}
<div>
    <div class="grid">
        <img src={{selectedItem.path}} alt="">
        <div class="info">
            <div>Name : {{selectedItem.name}}</div>
            <div>Author : {{selectedItem.author}}</div>
            <div>Year : {{selectedItem.year}}</div>
            <div>Size : {{selectedItem.size}}</div>
            <div>PLace : {{selectedItem.place}}</div>
        </div>
    </div>
    <div class="flex">
        <div *ngFor="let item of pictures"
             class="item" 
             [class.active]="item===selectedItem"
             (click)="ImgClick(item)">
            <img src={{item.path}} alt="">
        </div>
    </div>

</div>
