import { Component } from '@angular/core';
import { Picture } from 'src/app/models/picture';
import { PictureListService } from 'src/app/services/picture-list.service';
import { PictureComponent } from '../picture/picture.component';

@Component({
  selector: 'app-gallery',
  templateUrl: './gallery.component.html',
  styleUrls: ['./gallery.component.css'],
  providers:[PictureListService,PictureComponent]
})
export class GalleryComponent {
  pictures:Picture[]=[];
  selectedItem:Picture|undefined;


  ImgClick(picture:Picture){
    this.pictureComponent.SetPicture(picture);
  }


  constructor(private pictureListService:PictureListService,private pictureComponent:PictureComponent){

  }

  ngOnInit(){
    this.pictures=this.pictureListService.GetData();
    this.selectedItem=this.pictures[0];
  }
}
<div class="flex">
    <div *ngFor="let item of pictures"
         class="item" 
         [class.active]="item===selectedItem"
         (click)="ImgClick(item)">
        <img src={{item.path}} alt="">
    </div>
</div>
